#!/bin/sh

git filter-branch --env-filter '
an="$GIT_AUTHOR_NAME"
am="$GIT_AUTHOR_EMAIL"
cn="$GIT_COMMITTER_NAME"
cm="$GIT_COMMITTER_EMAIL"
reg="^([a-zA-Z0-9_\-\.\+]+)@163.com"
if [[ "$GIT_COMMITTER_EMAIL" =~ $reg ]]
then
    cn="charming-wolf"
    cm="c.witcher.w@gmail.com"
fi
if [[ "$GIT_AUTHOR_EMAIL" =~ $reg ]]
then
    an="charming-wolf"
    am="c.witcher.w@gmail.com"
fi
    export GIT_AUTHOR_NAME="$an"
    export GIT_AUTHOR_EMAIL="$am"
    export GIT_COMMITTER_NAME="$cn"
    export GIT_COMMITTER_EMAIL="$cm"
' -f